<?php

namespace ProjectBundle\Repository;

/**
 * PlaylistRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PlaylistRepository extends \Doctrine\ORM\EntityRepository
{
    public function findLatestPlaylists(){

        return $this->findBy(array('isDayli'=> 'false', 'isDefault' => "false"), array('dateAdd' => 'desc'), 5);

    }

    public function foundSoundsToCron($user, $maximum){

        return $this->createQueryBuilder('p')
            ->addSelect('p.id')
            ->innerJoin ('p.sounds', 'sd')
            ->andWhere('p.user = :user')
            ->andWhere('sd.id = :random')
            ->setParameters(array(
                'user'=> $user,
                "random" => $maximum))
            ->getQuery()
            ->getArrayResult();
    }
}
